import * as React from "react";
import { useState } from "react";
import { TextField, Typography, Box, Stack, Grid } from "@mui/material";
import { useNavigate } from "react-router-dom";
import InputAdornment from "@mui/material/InputAdornment";
import PersonOutlineOutlinedIcon from "@mui/icons-material/PersonOutlineOutlined";
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import HelpOutlineOutlinedIcon from '@mui/icons-material/HelpOutlineOutlined';
import { ButtonPrimary, SelectItem } from "../../components";
// import { ModalNotice, ModalLoad } from "../../components/Modal";
import { useSelector, useDispatch } from "react-redux";
import { useTranslation } from 'react-i18next';
// import { dataAction } from "../../redux/dataSlice";

import styles from "./LoginPage.module.scss";
import loginImage from "../../assets/images/thumbs/banner.png";
import { arrayBufferToBase64 } from "../../functions";
// import { downloadURL, imageURL } from "../../api";
// import { downloadConfig } from "../../data";

const height = window.innerHeight + "px";

const LoginPage = () => {

    const navigate = useNavigate();
    const dispatch = useDispatch();
    const lastLogin = localStorage.getItem("lastLogin") === null ? "" : localStorage.getItem("lastLogin");
    const [data, setData] = useState({
        "WH_CD": "",
        "EMP_ID": "",
        "PASS": "",
    });
    const [open, setOpen] = useState(false);
    const [showPass, setShowPass] = useState(false);

    const handleClick = () => {
        setShowPass(showPass => !showPass);
    }

    ////// Translate
    const { t } = useTranslation();

    ////// Open Modal
    // const openNotice = useSelector(state => state.commonData.openNotice);
    // const typeNotice = useSelector(state => state.commonData.typeNotice);

    ////// Handle Modal Notice
    const handleClose = () => {
        // dispatch(dataAction.closeNotice());
    }

    //////// Handle Set Controlled Data
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setData(event.target.value);

        setData(prevData => {
            return {
                ...prevData,
                [event.target.name]: event.target.value,
            }
        })
    };

    ////// Cancel Fetch API After Timeout
    const Timeout = (time) => {
        let controller = new AbortController();
        setTimeout(() => controller.abort(), time * 1000);
        return controller;
    };

    const handleSignIn = async() => {
        // const dataConfig = await downloadConfig("Q_EMP", data);
        // fetchDownload(dataConfig);
        navigate("/");
    }
    
    ////// Download User Info Data
    const fetchDownload = async (dataConfig) => {
        // setOpen(open => true);

        // fetch(downloadURL, {
        //     method: 'POST',
        //     mode: 'cors',
        //     dataType: "json",
        //     headers: {
        //         'Content-Type': 'application/json',
        //     },
        //     body: JSON.stringify(dataConfig),
        //     signal: Timeout(5).signal,
        // }).then((response) => {
        //     response.json().then(async(result) => {
        //         if(result.length > 0){
        //             await submitForm(result);
        //             setOpen(open => false);
        //             // Store
        //             sessionStorage.setItem("userData", JSON.stringify(result[0]));
        //             localStorage.setItem("lastLogin", data);
        //             fetchDownloadImg();
        //         }else{
        //             setOpen(open => false);
        //             dispatch(dataAction.setTypeNotice("not-found"));
        //             dispatch(dataAction.openNotice());
        //         }
        //     })  
        // }).catch(error => {
        //     setOpen(open => false);
        //     dispatch(dataAction.setTypeNotice("connect-failed"));
        //     dispatch(dataAction.openNotice());
        // });
    }

    ////// Download User Image
    const fetchDownloadImg = async () => {
        // fetch(imageURL, {
        //     method: 'POST',
        //     mode: 'cors',
        //     dataType: "json",
        //     headers: {
        //         'Content-Type': 'application/json',
        //     },
        //     body: JSON.stringify({
        //         "ARG_EMPID" : data,
        //         "OUT_CURSOR" : ""
        //       }),
        // }).then((response) => {
        //     response.json().then(async(result) => {
        //         if(result.length > 0){
        //             let imgData = await arrayBufferToBase64(result[0].PHOTO.data);
    
        //             if(imgData !== "" && imgData !== null){
        //                 sessionStorage.setItem("userImg", imgData);
        //                 navigate("/");
        //             }
        //         }
        //     })  
        // }).catch(error => {
        //     dispatch(dataAction.setTypeNotice("connect-failed"));
        //     dispatch(dataAction.openNotice());
        // });
    }

    function submitForm(result) {
        // Pretend it's hitting the network.
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                if (result.length > 0) {
                    resolve();
                } else {
                    reject();
                }
            }, 2000);
        });
    }

    return (
        <>
            <Box
                className={styles["s-layout"]}
                sx={{
                    width: "100%",
                    minHeight: height,
                }}
            >
                <Box className={styles["b-box"]}>
                    <Box className={styles["s-form"]}>
                        <Box className={styles["p-title"]}>
                            Warehouse <span>Management System</span>
                        </Box>
                        <Box className={styles["b-thumb"]}>
                            <img src={loginImage} alt="Login" />
                        </Box>
                        <form>
                            <Stack marginBottom={1.5}>
                                <SelectItem
                                    title="Warehouse"
                                    name="WH_CD"
                                    data={null}
                                    placeholder="Chọn kho hàng"
                                    cValue={data.WH_CD}
                                    handleEvent={() => {}}
                                    isValidate={true}
                                    message="Dữ liệu không được để trống" />
                            </Stack>
                            <Stack marginBottom={1.5}>
                                <TextField
                                    label="User ID"
                                    inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }}
                                    className={styles["b-input"]}
                                    placeholder="Nhập mã thẻ"
                                    value={data.EMP_ID}
                                    onChange={handleChange}
                                    name="EMP_ID"
                                    color="info"
                                    InputProps={{
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <PersonOutlineOutlinedIcon />
                                            </InputAdornment>
                                        ),
                                    }}
                                    fullWidth
                                />
                            </Stack>
                            <Stack marginBottom={1.5}>
                                <TextField
                                    label="Password"
                                    type={showPass ? "text" : "password"}
                                    inputProps={{ inputMode: 'text', }}
                                    className={styles["b-input"]}
                                    placeholder="Nhập mật khẩu"
                                    value={data.PASS}
                                    onChange={handleChange}
                                    name="PASS"
                                    color="info"
                                    InputProps={{
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <LockOutlinedIcon />
                                            </InputAdornment>
                                        ),
                                        endAdornment: (
                                            <InputAdornment position="end" sx={{ paddingLeft: 1.5, }} onClick={handleClick} >
                                                {showPass ? <VisibilityOffIcon />: <VisibilityIcon /> }
                                            </InputAdornment>
                                        ),
                                    }}
                                    fullWidth
                                />
                                <Typography className={styles["s-validate"]}>Password and ID is same for first time login</Typography>
                            </Stack>
                            <Box className={styles["s-bot"]}>
                                <ButtonPrimary title="Đăng nhập" handleClick={handleSignIn} name="key" />
                            </Box>
                        </form>
                    </Box>
                </Box>
            </Box>
        </>
    );
};

export default LoginPage;
