import { useState } from 'react';
import { Box, Container, Typography, Stack } from '@mui/material';
import { FormInput, ButtonPrimary } from '../../components';
import { useTranslation } from 'react-i18next';
import { useDispatch } from 'react-redux';
import { commonAction } from '../../redux/commonSlice';

import "./UserPage.scss";

const width = window.innerWidth;
const height = (width <= 479 ? window.innerHeight - 175 : window.innerHeight - 215) + "px";

const LoginPage = () => {

    ////// Basic Data
    const [data, setData] = useState({
        "OLD_PASS": {
            "ID": "OLD_PASS",
            "VALUE": "",
            "SHOW": false,
        },
        "NEW_PASS": {
            "ID": "NEW_PASS",
            "VALUE": "",
            "SHOW": false,
        },
        "CONFIRM_PASS": {
            "ID": "CONFIRM_PASS",
            "VALUE": "",
            "SHOW": false,
        },
    });

    ////// Translate
    const { t } = useTranslation();
    const dispatch = useDispatch();

    ///////Handle Change Text Field
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        let item = data[event.target.name];
        setData(prevData => {
            return{
                ...prevData,
                [event.target.name]: {
                    ...item,
                    "VALUE": event.target.value
                }
            }
        })
    }

    ///// Handle Confirm
    const handleClick = () => {
        dispatch(commonAction.setTypeNotice("under-construct"));
        dispatch(commonAction.openNotice());
    }

    ///// Handle Show
    const handleShow = (name) => {
        let item = data[name];
        let _result = !item.SHOW;
        console.log(_result)
        // setData(prevData => {
        //     return{
        //         ...prevData,
        //         [name]: {
        //             ...item,
        //             "SHOW": !item.SHOW
        //         }
        //     }
        // })
    }

    return (
        <>
            <Box className="s-home-3">
                <Box className="s-home-content">
                    <Container>
                        <Box className="s-form-content">
                            <Box sx={{ width: "100%", minHeight: height, }}>
                                <Box className="b-card-3">
                                    <Typography className="b-title">{t('change_title')}</Typography>
                                    <Typography className="b-desc">{t('change_desc')}</Typography>
                                </Box>
                                <Box className="s-form">
                                    <FormInput
                                        title={t('frm_oldpass')}
                                        placeholder={t('pl_oldpass')}
                                        value={data.OLD_PASS.VALUE}
                                        isDefault={true}
                                        name="OLD_PASS"
                                        disable={false}
                                        handleEvent={handleChange}
                                        isImportant={true}
                                        inputProp={{ inputMode: 'text',}}
                                        showPass={false}
                                        handleShow={handleShow}
                                        />
                                    <FormInput
                                        title={t('frm_newpass')}
                                        placeholder={t('pl_newpass')}
                                        value={data.NEW_PASS.VALUE}
                                        isDefault={true}
                                        name="NEW_PASS"
                                        disable={false}
                                        handleEvent={handleChange}
                                        isImportant={true}
                                        inputProp={{ inputMode: 'text',}}
                                        showPass={false}
                                        handleShow={handleShow}
                                        />
                                    <FormInput
                                        title={t('frm_confirmpass')}
                                        placeholder={t('pl_confirmpass')}
                                        value={data.CONFIRM_PASS.VALUE}
                                        isDefault={true}
                                        name="CONFIRM_PASS"
                                        disable={false}
                                        handleEvent={handleChange}
                                        isImportant={true}
                                        inputProp={{ inputMode: 'text',}}
                                        showPass={false}
                                        handleShow={handleShow}
                                        />
                                </Box>
                                <Box className="s-bot">
                                    <ButtonPrimary title={t('btn_confirm')} handleClick={handleClick} name="pass" />
                                </Box>
                            </Box>
                        </Box>
                    </Container>
                </Box>
            </Box>
        </>
    )
}

export default LoginPage;
