import * as React from "react";
import { useState, useEffect } from "react";
import { TextField, Typography, Box, Stack, Grid } from "@mui/material";
import { useNavigate } from "react-router-dom";
import InputAdornment from "@mui/material/InputAdornment";
import PersonOutlineOutlinedIcon from "@mui/icons-material/PersonOutlineOutlined";
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import HelpOutlineOutlinedIcon from '@mui/icons-material/HelpOutlineOutlined';
import { ButtonPrimary, SelectItem } from "../../components";
import { ModalNotice, ModalLoading } from "../../components/Modal";
// import { ModalNotice, ModalLoad } from "../../components/Modal";
import { useSelector, useDispatch } from "react-redux";
import { useTranslation } from 'react-i18next';
import { commonAction } from '../../redux/commonSlice';

import styles from "./LoginPage.module.scss";
import loginImage from "../../assets/images/thumbs/banner.png";
import { arrayBufferToBase64, fetchData } from "../../functions";
import { signinURL, downloadURL } from "../../api";
// import { downloadConfig } from "../../data";

const height = window.innerHeight + "px";

const LoginPage = () => {

    ///// Basic Data
    const lastLogin = localStorage.getItem("lastLogin") === null ? "" : localStorage.getItem("lastLogin");
    const [data, setData] = useState({
        WH_CD: "",
        EMP_ID: "",
        PASS: "",
    });
    const [whData, setWhData] = useState(null);
    const [showPass, setShowPass] = useState(false);

    const handleClick = () => {
        setShowPass(showPass => !showPass);
    }

    ////// Translate
    const { t } = useTranslation();

    ///// Navigate
    const navigate = useNavigate();
    const dispatch = useDispatch();

    ////// Open Modal
    const openNotice = useSelector(state => state.common.openNotice);
    const typeNotice = useSelector(state => state.common.typeNotice);
    const openLoad = useSelector(state => state.common.openLoad);

    ////// Handle Modal Notice
    const handleCloseNotice = () => {
        dispatch(commonAction.closeNotice());
    }

    //////// Handle Set Controlled Data
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setData(prevData => {
            return {
                ...prevData,
                [event.target.name]: event.target.value,
            }
        })
    };

    ////// Handle Login
    const handleSignIn = async() => {
        fetchDownload(signinURL, {
            ARG_TYPE: "Q",
            ARG_EMPID: data.EMP_ID,
            ARG_PASS: data.PASS,
            ARG_WH: data.WH_CD,
            OUT_CURSOR: "" 
        }, "login");
    }
    
    ////// Download User Info Data
    // const fetchDownload = async (url, dataConfig) => {
    //     const result = await fetchData(url, dataConfig);

    //     if(result){
    //         if(result.length > 0){
    //             console.log(result)
    //         }else{

    //         }
    //     }
    //     else{
    //         dispatch(commonAction.setTypeNotice("connect-failed"));
    //         dispatch(commonAction.openNotice());
    //     }
    // }

    const fetchDownload = async (url, dataConfig, type) => {
        const result = await fetchData(url, dataConfig);

        if(result){
            if(result.length > 0){
                switch(type){
                    case "warehouse":
                        setWhData(prevData => result);
                        ///// Deault Warehouse: IT
                        setData(prevData => {
                            return {
                                ...prevData,
                                WH_CD: '10',
                            }
                        });
                        break;
                    case "login":
                        console.log(result)
                        break;
                    default:
                        break;
                }
            }else{
                switch(type){
                    case "warehouse":
                        setWhData(prevData => null);
                        break;
                    default:
                        break;
                }
            }
        }else{
            dispatch(commonAction.setTypeNotice("connect-failed"));
            dispatch(commonAction.openNotice());
        }
    }

    ////// Download User Image
    const fetchDownloadImg = async () => {
        // fetch(imageURL, {
        //     method: 'POST',
        //     mode: 'cors',
        //     dataType: "json",
        //     headers: {
        //         'Content-Type': 'application/json',
        //     },
        //     body: JSON.stringify({
        //         "ARG_EMPID" : data,
        //         "OUT_CURSOR" : ""
        //       }),
        // }).then((response) => {
        //     response.json().then(async(result) => {
        //         if(result.length > 0){
        //             let imgData = await arrayBufferToBase64(result[0].PHOTO.data);
    
        //             if(imgData !== "" && imgData !== null){
        //                 sessionStorage.setItem("userImg", imgData);
        //                 navigate("/");
        //             }
        //         }
        //     })  
        // }).catch(error => {
        //     dispatch(dataAction.setTypeNotice("connect-failed"));
        //     dispatch(dataAction.openNotice());
        // });
    }

    function submitForm(result) {
        // Pretend it's hitting the network.
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                if (result.length > 0) {
                    resolve();
                } else {
                    reject();
                }
            }, 2000);
        });
    }

    ////// Default Events
    useEffect(() => {
        fetchDownload(downloadURL, {
            ARG_TYPE: "Q_WH",
            ARG_EMPID: "",
            OUT_CURSOR: "" 
        }, "warehouse");
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    ////// Handle Select
    const handleSelect = (name, value) => {
        setData(prevData => {
            return {
                ...prevData,
                [name]: value,
            }
        });
    }

    return (
        <>
            <Box
                className={styles["s-layout"]}
                sx={{
                    width: "100%",
                    minHeight: height,
                }}
            >
                <Box className={styles["b-box"]}>
                    <Box className={styles["s-form"]}>
                        <Box className={styles["p-title"]}>
                            Warehouse <span>Management System</span>
                        </Box>
                        <Box className={styles["b-thumb"]}>
                            <img src={loginImage} alt="Login" />
                        </Box>
                        <form>
                            <Stack marginBottom={1.5}>
                                <SelectItem
                                    title="Warehouse"
                                    name="WH_CD"
                                    data={whData}
                                    placeholder="Chọn kho hàng"
                                    cValue={data.WH_CD}
                                    handleEvent={handleSelect}
                                    isValidate={true}
                                    message="Dữ liệu không được để trống" />
                            </Stack>
                            <Stack marginBottom={1.5}>
                                <TextField
                                    label="User ID"
                                    inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }}
                                    className={styles["b-input"]}
                                    placeholder="Nhập mã thẻ"
                                    value={data.EMP_ID}
                                    onChange={handleChange}
                                    name="EMP_ID"
                                    color="info"
                                    InputProps={{
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <PersonOutlineOutlinedIcon />
                                            </InputAdornment>
                                        ),
                                    }}
                                    fullWidth
                                />
                            </Stack>
                            <Stack marginBottom={1.5}>
                                <TextField
                                    label="Password"
                                    type={showPass ? "text" : "password"}
                                    inputProps={{ inputMode: 'text', }}
                                    className={styles["b-input"]}
                                    placeholder="Nhập mật khẩu"
                                    value={data.PASS}
                                    onChange={handleChange}
                                    name="PASS"
                                    color="info"
                                    InputProps={{
                                        startAdornment: (
                                            <InputAdornment position="start">
                                                <LockOutlinedIcon />
                                            </InputAdornment>
                                        ),
                                        endAdornment: (
                                            <InputAdornment position="end" sx={{ paddingLeft: 1.5, }} onClick={handleClick} >
                                                {showPass ? <VisibilityOffIcon />: <VisibilityIcon /> }
                                            </InputAdornment>
                                        ),
                                    }}
                                    fullWidth
                                />
                                <Typography className={styles["s-validate"]}>Password and ID is same for first time login</Typography>
                            </Stack>
                            <Box className={styles["s-bot"]}>
                                <ButtonPrimary title="Đăng nhập" handleClick={handleSignIn} name="key" />
                            </Box>
                        </form>
                    </Box>
                </Box>
            </Box>
            <ModalNotice 
                open={openNotice} 
                handleClose={handleCloseNotice}
                type={typeNotice} />
            <ModalLoading open={openLoad} />
        </>
    );
};

export default LoginPage;
