import { useState, useEffect } from 'react';
import { Box, Container, Grid, Typography,InputAdornment, Stack, TextField } from '@mui/material';
import { useTranslation } from 'react-i18next';
import { useLocation } from 'react-router-dom';
import { ResultCard, ResultSkelton, EmptyCard, ButtonPrimary } from '../../components';
import { useNavigate } from 'react-router-dom';
import { fetchData, getDate, isNullOrEmpty, submitForm } from '../../functions';
import { getScanDataURL } from '../../api';
import SearchIcon from '@mui/icons-material/Search';

import "./ResultPage.scss";

const width = window.innerWidth;
const height = (width <= 479 ? window.innerHeight - 175 : window.innerHeight - 215) + "px";

const ResultPage = () => {

    ////// Basic Data
    const [data, setData] = useState(null);
    const [isEmpty, setIsEmpty] = useState(false);
    const [search, setSearch] = useState("");
    const [filter, setFilter] = useState(null); 

    ////// Navigate
    const navigate = useNavigate();

    ////// Translate
    const { t } = useTranslation();
    const skeltonRows = [1,2,3,4];

    ////// Get Data From Homepage
    const location = useLocation();
    const { plantCode, locCode, locName, whCode } = location?.state;

    ///// Handle Download
    const fetchDownload = async() => {
        setIsEmpty(prevData => false);
        await submitForm(true);

        const result = await fetchData(getScanDataURL, {
            V_P_SCAN        :"Q_WH",
            V_P_TYPE        : "",
            V_P_PLANT_CD    : plantCode,
            V_P_WH_CD 	    : whCode,
            V_P_PART_GROUP  : "ALL",
            V_P_PART_CD	    : "",
            V_P_YMD			: getDate(),
            V_P_LOC_WH      : locCode,
            V_P_USER 	    : "",
            V_P_IP          : "",
            OUT_CURSOR      : "" 
        });

        if(result){
            if(result.length > 1){
                result.shift();
                setData(prevData => result);
                setFilter(prevData => result);
            }else{
                setData(prevData => null);
                setFilter(prevData => null);
                setIsEmpty(prevData => true);
            }
        }else{
            setData(prevData => null);
            setFilter(prevData => null);
            setIsEmpty(prevData => true);
        }
    }

    useEffect(() => {
        if(location?.state === null){
            navigate("/", {replace: true});
        } 
        else if(isNullOrEmpty(plantCode) || isNullOrEmpty(locCode) || isNullOrEmpty(locName) || isNullOrEmpty(whCode)){
            navigate("/", {replace: true});
        }
        else{
            fetchDownload();
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    ///// Handle Back Home
    const handleClick = () => {
        navigate("/", {replace: true});
    }

    ///// Handle Filter
    const handleFilter = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearch(prevData => event.target.value);

        let _input = event.target.value.toUpperCase();
        const filtered = data.filter(item => {
            return item.PART_GROUP.toUpperCase().includes(_input) || item.PART.toUpperCase().includes(_input);
        });

        console.log(filtered)
    }

    return (
        <>
            <Box className="s-home-5">
                <Box className="s-home-content">
                    <Container>
                        <Box className="s-form-content">
                            <Box sx={{ width: "100%", minHeight: height, }}>
                                <Box className="b-card-3">
                                    <Typography className="b-title">{locName}</Typography>
                                    <Typography className="b-desc">{t('wh_result_desc')}</Typography>
                                </Box>

                                <form>
                                    <Stack marginBottom={1.5}>
                                        <TextField
                                            label=""
                                            inputProps={{ inputMode: 'text', }}
                                            className=""
                                            placeholder="Nhập mã thẻ"
                                            value={search}
                                            onChange={handleFilter}
                                            name="EMP_ID"
                                            color="info"
                                            InputProps={{
                                                startAdornment: (
                                                    <InputAdornment position="start">
                                                        <SearchIcon />
                                                    </InputAdornment>
                                                ),
                                            }}
                                            fullWidth
                                        />
                                    </Stack>
                                </form>
                                <Grid container spacing={1.5} className="s-list">
                                    {data !== null && data?.length > 0 && data.map((item, index) => {
                                        return (
                                            <Grid key={index} item xs={12} sm={6} className="s-list_item">
                                                <ResultCard 
                                                    order={item.STT}
                                                    title={item.PART_GROUP}
                                                    desc={item.PART}
                                                    status={item.ORDER_PART.toUpperCase()}
                                                    safeQty={item.SAFETY_INV}
                                                    stockQty={item.STOCK_QTY} />
                                            </Grid>
                                        );
                                    })}
                                    {data == null && skeltonRows.map((item, index) => {
                                        return (
                                            <Grid key={index} item xs={12} sm={6}>
                                                <ResultSkelton />
                                            </Grid>
                                        )
                                    })}
                                </Grid>
                                {isEmpty &&
                                    <>
                                        <EmptyCard />
                                        <Box className="s-bot">
                                            <ButtonPrimary title={t('btn_go_hom')} handleClick={handleClick} name="go-back" />
                                        </Box>
                                    </>
                                }
                            </Box>
                        </Box>
                    </Container>
                </Box>
            </Box>
        </>
    )
}

export default ResultPage;
