import { useEffect, useState } from 'react';
import { Box, Container, Grid, Typography } from '@mui/material';
import { ButtonPrimary, NoticeCard, CameraScan } from '../../components';
import { useTranslation } from 'react-i18next';
import { useDispatch } from 'react-redux';
import { commonAction } from '../../redux/commonSlice';
import QrCode2Icon from '@mui/icons-material/QrCode2';

import { isNullOrEmpty } from '../../functions';

import "./HomePage.scss";
const width = window.innerWidth;
const height = (width <= 479 ?  window.innerHeight - 175 : window.innerHeight - 215) + "px";

const HomePage = () => {

    ////// Init Data
    const [data, setData] = useState(null);
    const [isValid, setIsValid] = useState(false);

    ////// Handle Camera
    const [openCamera, setOpenCamera] = useState(false);

    ////// Translate
    const { t } = useTranslation();

    ////// Dispatch
    const dispatch = useDispatch();

    //////Handle Internet Connect
    const testConnection = async () => {
        try {
            // const response = await fetch(validConnectURl, {
            //     method: 'POST',
            //     mode: 'cors',
            //     dataType: "json",
            //     headers: {
            //         'Content-Type': 'application/json',
            //     },
            //     signal: Timeout(5).signal,
            //     body: JSON.stringify({
            //         OUT_CURSOR: "",
            //     }),
            // });

            // const json = await response.json();
            // return json?.length > 0 ? true : false;
        }
        catch {
            return false;
        }
    }

    //////Camera Option
    const handleOpenCamera = async () => {

        dispatch(commonAction.openLoad());
        let _isConnect = await testConnection();

        if (_isConnect) {
            dispatch(commonAction.closeLoad());
            setData(prev => null);
            setIsValid(prev => false);
            setOpenCamera(prev => true);
        } else {
            dispatch(commonAction.closeLoad());
            dispatch(commonAction.setTypeNotice("connect-failed"));
            dispatch(commonAction.openNotice());
        }
    }

    const handleCloseCamera = () => {
        setOpenCamera(prev => false);
    }

    const handleScanSuccess = async (decodedText, decodedResult) => {
        if (!isNullOrEmpty(decodedText)) {
            // await fetchDownload(validateURL, {
            //     ARG_TYPE: "Q",
            //     ARG_BARCODE: decodedText,
            //     OUT_CURSOR: "",
            // }, "scanData");
        }
    }

    /////// Validate Barcode
    const fetchDownload = async (url, dataConfig, type) => {
        await fetch(url, {
            method: 'POST',
            mode: 'cors',
            dataType: "json",
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(dataConfig),
        }).then((response) => {
            response.json().then(async (result) => {
                if (result?.length > 0) {
                    switch (type) {
                        case "scanData":
                            // setData(prev => {
                            //     return {
                            //         PLANT_CD: result[0].PLANT_CD,
                            //         PLANT_NM: result[0].PLANT_NM,
                            //         FA_WC_CD: result[0].FA_WC_CD,
                            //         ERP_FA_WC_CD: result[0].ERP_FA_WC_CD,
                            //         LINE_NM: result[0].LINE_NM,
                            //         REQ_DATE: result[0].REQ_DATE,
                            //         STYLE_CD: result[0].STYLE_CD,
                            //         ITEM_CD: result[0].ITEM_CD,
                            //         ITEM_NM: result[0].ITEM_NM,
                            //         REQ: result[0].REQ,
                            //         ACT: result[0].ACT,
                            //         LOCATE_CD: result[0].LOCATE_CD,
                            //         SEQ: result[0].SEQ,
                            //         TRIP_CD: result[0].TRIP_CD,
                            //         BARCODE: result[0].BARCODE,
                            //     }
                            // });
                            // setIsValid(prev => true);
                            // setOpenCamera(prev => false);
                            // dispatch(commonAction.setScanStatus(true));
                            break;
                        default:
                            break;
                    }
                } else {
                    setOpenCamera(false);
                    setData("not-found");
                }
            })
        }).catch(error => {
            dispatch(commonAction.setTypeNotice("connect-failed"));
            dispatch(commonAction.openNotice());
        });
    }

    //////Cancel Fetch API After Timeout
    const Timeout = (time) => {
        let controller = new AbortController();
        setTimeout(() => controller.abort(), time * 1000);
        return controller;
    };

    return (
        <>
            <Box className="s-home-2">
                <Box className="s-home-content">
                    <Container>
                        <Box className="s-form-content">
                            <Box sx={{ width: "100%", minHeight: height, }}>
                                <Box className="b-card-3">
                                    <Typography className="b-title">{t('notify_title')}</Typography>
                                    <Typography className="b-desc">{t('notify_desc')}</Typography>
                                </Box>
                                <Box sx={{ textAlign: "center", position: "relative", marginBottom: 5 }}>
                                    <QrCode2Icon style={{ fontSize: 70 }} />
                                    <Box className="b-qr-border"></Box>
                                </Box>
                                <Grid container spacing={1.5}>
                                    <Grid item xs={6}>
                                        <div class="card">
                                        <div class="card-front">
                                            <p class="title">Warehouse</p>
                                            <p class="subtitle">Location</p>
                                        </div>
                                        </div>
                                    </Grid>
                                </Grid>
                                {/* <Box className="s-bot s-bot-sec">
                                    <ButtonPrimary title={t('btn_scan')} handleClick={handleOpenCamera} name="scan" />
                                </Box> */}
                                <Box sx={{ display: !isValid && data === "not-found" ? "block" : "none" }}>
                                    <NoticeCard />
                                </Box>
                            </Box>
                        </Box>
                    </Container>
                </Box>
            </Box>
            {openCamera &&
                <CameraScan open={openCamera} handleSuccess={handleScanSuccess} handleClose={handleCloseCamera} />
            }
        </>
    );
}

export default HomePage;
