import { useEffect, useState } from 'react';
import { Box, Container, Grid, Typography } from '@mui/material';
import { FormInput, ButtonPrimary, NoticeCard, CameraScan } from '../../components';
import { useTranslation } from 'react-i18next';
import { useSelector, useDispatch } from 'react-redux';
import { commonAction } from '../../redux/commonSlice';
import QrCode2Icon from '@mui/icons-material/QrCode2';

import { validateURL, validConnectURl, uploadURL } from '../../api';
import { isNullOrEmpty, getDateFormat } from '../../functions';

import "./HomePage.scss";

const width = window.innerWidth;
const height = window.innerHeight - 175 + "px";

const HomePage = () => {

    ////// Init Data
    const [data, setData] = useState(null);
    const [isValid, setIsValid] = useState(false);
    const [validAct, setValidAct] = useState({
        validate: true,
        messageEN: "The input field must be number",
        messageVN: "Dữ liệu nhập phải là số",
    });

    ////// Handle Camera
    const [openCamera, setOpenCamera] = useState(false);

    ////// Translate
    const { t } = useTranslation();
    const lang = useSelector(state => state.common.lang);
    const status = useSelector(state => state.common.scanStatus);
    const colSpacing = width > 479 ? 2 : 1;

    //////
    useEffect(() => {
        if(!status){
            setData(prev => null);
            setIsValid(prev => false);
            setOpenCamera(prev => true);
        }
    },[status]);

    ////// Dispatch
    const dispatch = useDispatch();

    //////Handle Internet Connect
    const testConnection = async () => {
        try {
            const response = await fetch(validConnectURl, {
                method: 'POST',
                mode: 'cors',
                dataType: "json",
                headers: {
                    'Content-Type': 'application/json',
                },
                signal: Timeout(5).signal,
                body: JSON.stringify({
                    OUT_CURSOR: "",
                }),
            });

            const json = await response.json();
            return json?.length > 0 ? true : false;
        }
        catch {
            return false;
        }
    }

    //////Camera Option
    const handleOpenCamera = async () => {

        dispatch(commonAction.openLoad());
        let _isConnect = await testConnection();

        if (_isConnect) {
            dispatch(commonAction.closeLoad());
            setData(prev => null);
            setIsValid(prev => false);
            setOpenCamera(prev => true);
        } else {
            dispatch(commonAction.closeLoad());
            dispatch(commonAction.setTypeNotice("connect-failed"));
            dispatch(commonAction.openNotice());
        }
    }

    const handleCloseCamera = () => {
        setOpenCamera(prev => false);
    }

    const handleScanSuccess = async (decodedText, decodedResult) => {
        if (!isNullOrEmpty(decodedText)) {
            await fetchDownload(validateURL, {
                ARG_TYPE: "Q",
                ARG_BARCODE: decodedText,
                OUT_CURSOR: "",
            }, "scanData");
        }
    }

    /////// Validate Barcode
    const fetchDownload = async (url, dataConfig, type) => {
        await fetch(url, {
            method: 'POST',
            mode: 'cors',
            dataType: "json",
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(dataConfig),
        }).then((response) => {
            response.json().then(async (result) => {
                if (result?.length > 0) {
                    switch (type) {
                        case "scanData":
                            setData(prev => {
                                return {
                                    PLANT_CD: result[0].PLANT_CD,
                                    PLANT_NM: result[0].PLANT_NM,
                                    FA_WC_CD: result[0].FA_WC_CD,
                                    ERP_FA_WC_CD: result[0].ERP_FA_WC_CD,
                                    LINE_NM: result[0].LINE_NM,
                                    REQ_DATE: result[0].REQ_DATE,
                                    STYLE_CD: result[0].STYLE_CD,
                                    ITEM_CD: result[0].ITEM_CD,
                                    ITEM_NM: result[0].ITEM_NM,
                                    REQ: result[0].REQ,
                                    ACT: result[0].ACT,
                                    LOCATE_CD: result[0].LOCATE_CD,
                                    SEQ: result[0].SEQ,
                                    TRIP_CD: result[0].TRIP_CD,
                                    BARCODE: result[0].BARCODE,
                                }
                            });
                            setIsValid(prev => true);
                            setOpenCamera(prev => false);
                            dispatch(commonAction.setScanStatus(true));
                            break;
                        default:
                            break;
                    }
                } else {
                    setOpenCamera(false);
                    setData("not-found");
                }
            })
        }).catch(error => {
            dispatch(commonAction.setTypeNotice("connect-failed"));
            dispatch(commonAction.openNotice());
        });
    }

    //////Cancel Fetch API After Timeout
    const Timeout = (time) => {
        let controller = new AbortController();
        setTimeout(() => controller.abort(), time * 1000);
        return controller;
    };

    ////// Submit Data
    const handleSubmit = async () => {
        let _valid = await handleValidate(data?.ACT);

        if (_valid) {
            dispatch(commonAction.openLoad());
            let _isConnect = await testConnection();

            if (_isConnect) {
                ///////// Upload Data
                let _update_config = {
                    "ARG_TRIP": data.TRIP_CD,
                    "ARG_FACTORY": data.PLANT_CD,
                    "ARG_LINE": data.FA_WC_CD,
                    "ARG_MLINE": data.ERP_FA_WC_CD,
                    "ARG_STYLE": data.STYLE_CD,
                    "ARG_ITEM_CD": data.ITEM_CD,
                    "ARG_LOCATE_CD": data.LOCATE_CD,
                    "ARG_REQ_DATE": data.REQ_DATE,
                    "ARG_ACT": data.ACT,
                    "ARG_BARCODE": data.BARCODE,
                    "ARG_USER": "VJ-V-MOBILE/LAYER_OUTGOING_SCAN/::1"
                }

                //////// Upload Gallery Image
                await fetch(uploadURL, {
                    method: 'POST',
                    mode: 'cors',
                    dataType: "json",
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(_update_config),
                }).then(async (response) => {
                    if (response.status === 200) {
                        dispatch(commonAction.closeLoad());
                        dispatch(commonAction.setTypeNotice("success"));
                        dispatch(commonAction.openNotice());
                    } else {
                        dispatch(commonAction.closeLoad());
                        dispatch(commonAction.setTypeNotice("error"));
                        dispatch(commonAction.openNotice());
                    }
                }).catch(error => {
                    dispatch(commonAction.closeLoad());
                    dispatch(commonAction.setTypeNotice("error"));
                    dispatch(commonAction.openNotice());
                });
            } else {
                dispatch(commonAction.closeLoad());
                dispatch(commonAction.setTypeNotice("connect-failed"));
                dispatch(commonAction.openNotice());
            }
        }
    }

    ////// Cancel All
    const handleCancel = () => {
        handleOpenCamera();
    }

    ////// Handle Change
    const handleChange = async (e) => {
        setData(prev => {
            return {
                ...prev,
                [e.target.name]: e.target.value,
            }
        });

        await handleValidate(e.target.value);
    }

    ////// Validate Data
    const handleValidate = (val) => {
        if (isNullOrEmpty(val)) {
            setValidAct(prev => {
                return {
                    ...prev,
                    validate: false,
                    messageEN: "The input field must be number",
                    messageVN: "Dữ liệu nhập phải là số",
                }
            });
            return false;
        }
        if (parseInt(data?.REQ, 10) * 1.2 < parseInt(val, 10)) {
            setValidAct(prev => {
                return {
                    ...prev,
                    validate: false,
                    messageEN: "The input field must not greater than 120% of Request Qty",
                    messageVN: "Dữ liệu nhập không lớn hơn 120% của số lượng yêu cầu",
                }
            });
            return false;
        }

        if (parseInt(val, 10) === 0) {
            setValidAct(prev => {
                return {
                    ...prev,
                    validate: false,
                    messageEN: "The input field must not greater than 0",
                    messageVN: "Dữ liệu nhập không lớn hơn 0",
                }
            });
            return false;
        }

        setValidAct(prev => {
            return {
                ...prev,
                validate: true,
            }
        });
        return true;
    }

    return (
        <>
            <Box className="s-home-2">
                <Box className="s-home-content">
                    <Container>
                        <Box className="s-form-content">
                            {!isValid &&
                                <Box sx={{ width: "100%", minHeight: height, }}>
                                    <Box className="b-card-3">
                                        <Typography className="b-title">{t('notify_title')}</Typography>
                                        <Typography className="b-desc">{t('notify_desc')}</Typography>
                                    </Box>
                                    <Box sx={{ textAlign: "center", position: "relative" }}>
                                        <QrCode2Icon style={{ fontSize: 200 }} />
                                        <Box className="b-qr-border"></Box>
                                    </Box>
                                    <Box className="s-bot s-bot-sec">
                                        <ButtonPrimary title={t('btn_scan')} handleClick={handleOpenCamera} name="scan" />
                                    </Box>
                                    <Box sx={{ display: !isValid && data === "not-found" ? "block" : "none" }}>
                                        <NoticeCard />
                                    </Box>
                                </Box>
                            }
                            {isValid && data !== null &&
                                <Box sx={{ width: "100%", minHeight: window.innerHeight - 165 + "px", }}>
                                    <form>
                                        <Grid container spacing={colSpacing} className="s-home-block">
                                            <Grid item xs={12}>
                                                <FormInput
                                                    title={t('frm_fty')}
                                                    placeholder=""
                                                    value={data?.PLANT_NM}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    isDefault={true} />
                                            </Grid>
                                        </Grid>
                                        <Grid container spacing={colSpacing} className="s-home-block">
                                            <Grid item xs={7} md={4}>
                                                <FormInput
                                                    title={t('frm_line')}
                                                    placeholder=""
                                                    value={data?.LINE_NM}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    isDefault={true} />
                                            </Grid>
                                            <Grid item xs={5} md={4}>
                                                <FormInput
                                                    title={t('frm_loc')}
                                                    placeholder=""
                                                    name="LOCATE_CD"
                                                    value={data?.LOCATE_CD}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    isDefault={true} />
                                            </Grid>
                                            <Grid item xs={12} md={4}>
                                                <FormInput
                                                    title={t('frm_req_date')}
                                                    placeholder=""
                                                    value={getDateFormat(data?.REQ_DATE)}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    isDefault={true} />
                                            </Grid>
                                        </Grid>
                                        {/* <Grid container spacing={colSpacing} className="s-home-block">
                                        <Grid item xs={12} md={6}>
                                            <FormInput
                                                title={t('frm_style')}
                                                placeholder=""
                                                value={data?.STYLE_CD}
                                                disable={true}
                                                inputProp={{ inputMode: 'text' }}
                                                isImportant={true}
                                                isDefault={true} />
                                        </Grid>
                                        <Grid item xs={12} md={6}>
                                            <FormInput
                                                title={t('frm_item_cd')}
                                                placeholder=""
                                                value={data?.ITEM_CD}
                                                disable={true}
                                                inputProp={{ inputMode: 'text' }}
                                                isImportant={true}
                                                isDefault={true} />
                                        </Grid>
                                    </Grid> */}
                                        <Grid container spacing={colSpacing} className="s-home-block">
                                            <Grid item xs={12}>
                                                <FormInput
                                                    title={t('frm_item_nm')}
                                                    placeholder=""
                                                    value={data?.ITEM_NM}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    multiline={2}
                                                    isDefault={true} />
                                            </Grid>
                                        </Grid>
                                        <Grid container spacing={colSpacing} className="s-home-block">
                                            <Grid item xs={6} md={6}>
                                                <FormInput
                                                    title={"SEQ"}
                                                    placeholder=""
                                                    value={data?.SEQ}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    isDefault={true} />
                                            </Grid>
                                            <Grid item xs={6} md={6}>
                                                <FormInput
                                                    title={t('frm_trip')}
                                                    placeholder=""
                                                    value={data?.TRIP_CD}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    isDefault={true} />
                                            </Grid>
                                        </Grid>
                                        <Grid container spacing={colSpacing} className="s-home-block">
                                            <Grid item xs={12} md={6}>
                                                <FormInput
                                                    title={t('frm_req')}
                                                    placeholder=""
                                                    value={data?.REQ}
                                                    disable={true}
                                                    inputProp={{ inputMode: 'text' }}
                                                    isImportant={true}
                                                    isDefault={true} />
                                            </Grid>
                                            <Grid item xs={12} md={6}>
                                                <FormInput
                                                    title={t('frm_out')}
                                                    placeholder=""
                                                    name="ACT"
                                                    value={data?.ACT}
                                                    handleEvent={handleChange}
                                                    inputProp={{ inputMode: 'numeric', pattern: '[0-9]*' }}
                                                    isImportant={true}
                                                    isValidate={validAct.validate}
                                                    message={lang === "en" ? validAct.messageEN : validAct.messageVN}
                                                    isDefault={true} />
                                            </Grid>
                                        </Grid>
                                        <Box className="s-bot">
                                            <ButtonPrimary title={t('btn_cancel')} name="cancel" handleClick={handleCancel} />
                                            <ButtonPrimary title={t('btn_submit')} name="submit" handleClick={handleSubmit} />
                                        </Box>
                                    </form>
                                </Box>
                            }
                        </Box>
                    </Container>
                </Box>
            </Box>
            {openCamera &&
                <CameraScan open={openCamera} handleSuccess={handleScanSuccess} handleClose={handleCloseCamera} />
            }
        </>
    );
}

export default HomePage;
