import { useState, useEffect } from 'react';
import { Box, Container, Grid, Typography } from '@mui/material';
import { useTranslation } from 'react-i18next';
import { useLocation } from 'react-router-dom';
import { ResultCard, ResultSkelton, EmptyCard, ButtonPrimary } from '../../components';
import { useNavigate } from 'react-router-dom';
import { fetchData, getDate, isNullOrEmpty, submitForm } from '../../functions';
import { getScanDataURL } from '../../api';

import "./ResultPage.scss";

const width = window.innerWidth;
const height = (width <= 479 ? window.innerHeight - 175 : window.innerHeight - 215) + "px";

const ResultPage = () => {

    ////// Basic Data
    const [data, setData] = useState(null);
    const [isEmpty, setIsEmpty] = useState(false);

    ////// Navigate
    const navigate = useNavigate();

    ////// Translate
    const { t } = useTranslation();

    ////// Get Data From Homepage
    const location = useLocation();
    const { plantCode, locCode, locName, whCode } = location?.state;

    ///// Handle Download
    const fetchDownload = async() => {
        setIsEmpty(prevData => false);
        await submitForm(true);

        const result = await fetchData(getScanDataURL, {
            V_P_SCAN        :"Q_WH",
            V_P_TYPE        : "",
            V_P_PLANT_CD    : plantCode,
            V_P_WH_CD 	    : whCode,
            V_P_PART_GROUP  : "ALL",
            V_P_PART_CD	    : "",
            V_P_YMD			: getDate(),
            V_P_LOC_WH      : locCode,
            V_P_USER 	    : "",
            V_P_IP          : "",
            OUT_CURSOR      : "" 
        });

        if(result){
            if(result.length > 1){
                result.shift();
                setData(prevData => result);
            }else{
                setData(prevData => null);
                setIsEmpty(prevData => true);
            }
        }else{
            setData(prevData => null);
            setIsEmpty(prevData => true);
        }
    }

    useEffect(() => {
        if(location?.state === null){
            navigate("/", {replace: true});
        } 
        else if(isNullOrEmpty(plantCode) || isNullOrEmpty(locCode) || isNullOrEmpty(locName) || isNullOrEmpty(whCode)){
            navigate("/", {replace: true});
        }
        else{
            fetchDownload();
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    return (
        <>
            <Box className="s-home-5">
                <Box className="s-home-content">
                    <Container>
                        <Box className="s-form-content">
                            <Box sx={{ width: "100%", minHeight: height, }}>
                                <Box className="b-card-3">
                                    <Typography className="b-title">{locName}</Typography>
                                    <Typography className="b-desc">{t('wh_result_desc')}</Typography>
                                </Box>
                                {/* <Grid container spacing={1.5} className="s-list">
                                    {data !== null && data?.length > 0 && data.map((item, index) => {
                                        return (
                                            <Grid key={index} item xs={12} sm={6} className="s-list_item">
                                                <ResultCard 
                                                    order={item.STT}
                                                    title={item.PART_GROUP}
                                                    desc={item.PART}
                                                    status={item.ORDER_PART.toUpperCase()}
                                                    safeQty={item.SAFETY_INV}
                                                    stockQty={item.STOCK_QTY} />
                                            </Grid>
                                        );
                                    })}
                                    {data == null && 
                                        <>
                                            <Grid item xs={12} sm={6}>
                                                <ResultSkelton />
                                            </Grid>
                                            <Grid item xs={12} sm={6}>
                                                <ResultSkelton />
                                            </Grid>
                                            <Grid item xs={12} sm={6}>
                                                <ResultSkelton />
                                            </Grid>
                                            <Grid item xs={12} sm={6}>
                                                <ResultSkelton />
                                            </Grid>
                                        </>
                                    }
                                </Grid> */}
                                <EmptyCard />
                                <Box className="s-bot">
                                    <ButtonPrimary title={t('btn_scan')} handleClick={() => {}} name="scan" />
                                </Box>
                            </Box>
                        </Box>
                    </Container>
                </Box>
            </Box>
        </>
    )
}

export default ResultPage;
