import { useState, useEffect } from 'react';
import { Box, Container, Grid, Typography, Stack } from '@mui/material';
import { useTranslation } from 'react-i18next';
import { useLocation } from 'react-router-dom';
import { ResultCard } from '../../components';
import { useNavigate } from 'react-router-dom';
import { fetchData, getDate } from '../../functions';
import { getScanDataURL } from '../../api';

import "./ResultPage.scss";

const width = window.innerWidth;
const height = (width <= 479 ? window.innerHeight - 175 : window.innerHeight - 215) + "px";

const ResultPage = () => {

    ////// Basic Data
    const [data, setData] = useState(null);

    ////// Navigate
    const navigate = useNavigate();

    ////// Translate
    const { t } = useTranslation();

    ////// Get Data From Homepage
    const location = useLocation();
    const { locCode, locName, whCode } = location?.state;

    ///// Handle Download
    const fetchDownload = async() => {
        const result = await fetchData(getScanDataURL, {
            V_P_SCAN        :"Q_WH",
            V_P_TYPE        : "",
            V_P_PLANT_CD    : "2110",
            V_P_WH_CD 	    : whCode,
            V_P_PART_GROUP  : "ALL",
            V_P_PART_CD	    : "",
            V_P_YMD			: getDate(),
            V_P_LOC_WH      : locCode,
            V_P_USER 	    : "",
            V_P_IP          : "",
            OUT_CURSOR      : "" 
        });

        if(result){
            if(result.length > 1){
                result.shift();
                setData(prevData => result);
                console.log(result)
            }else{
                setData(prevData => null);
            }
        }else{
            setData(prevData => null);
        }
    }

    useEffect(() => {
        if(location?.state === null){
            navigate("/", {replace: true});
        }else{
            fetchDownload();
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    return (
        <>
            <Box className="s-home-5">
                <Box className="s-home-content">
                    <Container>
                        <Box className="s-form-content">
                            <Box sx={{ width: "100%", minHeight: height, }}>
                                <Box className="b-card-3">
                                    <Typography className="b-title">{locName}</Typography>
                                    <Typography className="b-desc">{t('wh_result_desc')}</Typography>
                                </Box>
                                <Grid container spacing={1.5} className="s-list">
                                    {data !== null && data?.length > 0 && data.map((item, index) => {
                                        return (
                                            <Grid key={index} item xs={12} sm={6} className="s-list_item">
                                                <ResultCard 
                                                    order={item.STT}
                                                    title={item.PART_GROUP}
                                                    desc={item.PART}
                                                    status={item.ORDER_PART.toUpperCase()}
                                                    safeQty={item.SAFETY_INV}
                                                    stockQty={item.STOCK_QTY} />
                                            </Grid>
                                        );
                                    })}
                                </Grid>
                            </Box>
                        </Box>
                    </Container>
                </Box>
            </Box>
        </>
    )
}

export default ResultPage;
